"use strict";

$(".carousel-main").flickity({
  cellAlign: "left",
  contain: true,
  wrapAround: true,
});

// Artikel hover lässt Bild erscheinen
var article_links = $(".article_link");
var article_images = $(".article_image");
var intros = $(".intro");
var themas = $(".thema");
var t1, t2, t3;
var icons = $(".icon_container");
var wrapper = $(".wrapper");
var bottomWrap = $(".bottom_wrapper");
var logo = $(".logo");
var articleContainer = $(".article_container");
var holding = false;

article_links.each(function (index) {
  $(article_links[index]).hover(
    function () {
      logo.addClass("logo_img_active");
      let i = index;
      $(article_images[index]).addClass("active_image");
      t1 = setTimeout(function () {
        $(intros[i]).addClass("active_text");
      }, 200);
      t2 = setTimeout(function () {
        $(themas[index]).addClass("active_text");
      }, 400);
    },
    function () {
      $(article_images[index]).removeClass("active_image");
      $(intros[index]).removeClass("active_text");
      $(themas[index]).removeClass("active_text");
      logo.removeClass("logo_img_active");
      clearTimeout(t1);
      clearTimeout(t2);
    }
  );
});

